// Generated by Selenium IDE
const { Builder, By, Key, until } = require('selenium-webdriver')
const assert = require('assert')

describe('CP004-Validación del Formato de Email', function() {
  this.timeout(30000)
  let driver
  let vars
  beforeEach(async function() {
    driver = await new Builder().forBrowser('chrome').build()
    vars = {}
  })
  afterEach(async function() {
    await driver.quit();
  })
  it('CP004-Validación del Formato de Email', async function() {
    // Test name: CP004-Validación del Formato de Email
    // Step # | name | target | value
    // 1 | open | /auth/sign-in | 
    await driver.get("https://test-qa.inlaze.com/auth/sign-in/auth/sign-in")
    // 2 | setWindowSize | 1528x808 | 
    await driver.manage().window().setRect({ width: 1528, height: 808 })
    // 3 | click | linkText=Sign up | 
    await driver.findElement(By.linkText("Sign up")).click()
    // 4 | click | id=full-name | 
    await driver.findElement(By.id("full-name")).click()
    // 5 | type | id=full-name | Monica Gallego
    await driver.findElement(By.id("full-name")).sendKeys("Monica Gallego")
    // 6 | click | id=email | 
    await driver.findElement(By.id("email")).click()
    // 7 | type | id=email | monica.gallego.com
    await driver.findElement(By.id("email")).sendKeys("monica.gallego.com")
    // 8 | click | css=.w-full > #password | 
    await driver.findElement(By.css(".w-full > #password")).click()
    // 9 | type | css=.w-full > #password | Monica1234*
    await driver.findElement(By.css(".w-full > #password")).sendKeys("Monica1234*")
    // 10 | click | css=#password .btn | 
    await driver.findElement(By.css("#password .btn")).click()
    // 11 | click | css=.w-full > #confirm-password | 
    await driver.findElement(By.css(".w-full > #confirm-password")).click()
    // 12 | type | css=.w-full > #confirm-password | Monica1234*
    await driver.findElement(By.css(".w-full > #confirm-password")).sendKeys("Monica1234*")
    // 13 | click | css=.fa-eye | 
    await driver.findElement(By.css(".fa-eye")).click()
    // 14 | click | css=.btn:nth-child(5) | 
    await driver.findElement(By.css(".btn:nth-child(5)")).click()
  })
})
